%  Auto-generated by MATLAB on 15-Feb-2016 15:24:54


%% Fit: 'untitled fit 1'.
[xData, yData] = prepareCurveData( x, y );

% Set up fittype and options.
ft = fittype( 'amp*real(sqrt(Rad^2-(x-x0)^2))+bg', 'independent', 'x', 'dependent', 'y' );
opts = fitoptions( 'Method', 'NonlinearLeastSquares' );
opts.Algorithm = 'Levenberg-Marquardt';
opts.Display = 'Off';
opts.Robust = 'Bisquare';
opts.StartPoint = [45 0.03 0 70];

% Fit model to data.
[fitresult, gof] = fit( xData, yData, ft, opts );
edgeleft = fitresult.x0 - fitresult.Rad;
edgeright = fitresult.x0 + fitresult.Rad;

